package main

import (
	"fmt"
	"github.com/hashgraph/hedera-state-proof-verifier-go/stateproof"
)

// this example verifies a V2 record stream
func main() {
	bytes := []byte(
		`{
			    "address_books": [
			        ""
			    ],
			    "record_file": "",
			    "signature_files": {
			        "0.0.5": "",
			        "0.0.6": "",
			        "0.0.3": "",
			        "0.0.4": ""
			    },
			    "version": 2
		}`)

	res, err := stateproof.Verify("0.0.1893-1605177623-307000000", bytes)
	if err != nil {
		panic(err)
	}
	fmt.Println(res)
}
